import javafx.application.Application;
import javafx.stage.*;
public class Main extends Application {
	
	
	
	@Override
	public void start(Stage primaryStage) {
		/*set the Title of window which is shown as name
	    of the application. Where it actually shows is on the top
	    the window.*/
		primaryStage.setTitle("State Diagram Editor");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		launch(args);
	}

}
